/*Table structure for table `articles` */

DROP TABLE IF EXISTS `articles`;

CREATE TABLE `articles` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `meta_title` varchar(255) DEFAULT NULL COMMENT 'meta_title',
  `meta_keywords` varchar(255) DEFAULT NULL COMMENT 'meta_keywords',
  `meta_description` varchar(255) DEFAULT NULL COMMENT 'meta_description',
  `data` text COMMENT 'Содержимое',
  `date` date DEFAULT NULL COMMENT 'Дата',
  `title` varchar(255) DEFAULT NULL COMMENT 'Заголовок',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `prev_text` text COMMENT 'Текст предпросмотра',
  `slug` varchar(255) DEFAULT NULL COMMENT 'Адрес',
  PRIMARY KEY (`id`),
  KEY `status_id` (`status_id`)
) ENGINE=InnoDB AUTO_INCREMENT=155 DEFAULT CHARSET=utf8;

