/*Table structure for table `feedback` */

DROP TABLE IF EXISTS `feedback`;

CREATE TABLE `feedback` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `city` varchar(255) DEFAULT NULL COMMENT 'Город',
  `phone` varchar(255) DEFAULT NULL COMMENT 'Телефон',
  `email` varchar(255) DEFAULT NULL COMMENT 'E-mail',
  `name` varchar(255) DEFAULT NULL COMMENT 'ФИО',
  `text` text COMMENT 'Текст сообщения',
  `type` int(16) DEFAULT NULL COMMENT 'Тип',
  `subject` varchar(255) DEFAULT NULL COMMENT 'Объект запроса',
  `status` int(11) DEFAULT '1' COMMENT 'Статус',
  `date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Дата',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=utf8;

