/*Table structure for table `map` */

DROP TABLE IF EXISTS `map`;

CREATE TABLE `map` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `city` varchar(255) NOT NULL COMMENT 'Город',
  `x` float NOT NULL COMMENT 'Положение по оси X в процентах',
  `y` float NOT NULL COMMENT 'Положение по оси Y в процентах',
  `lat` float NOT NULL COMMENT 'Широта',
  `lng` float NOT NULL COMMENT 'Долгота',
  `text` varchar(255) DEFAULT NULL COMMENT 'Текст балуна',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `zoom` int(11) NOT NULL COMMENT 'Увеличение',
  PRIMARY KEY (`id`),
  KEY `status_id` (`status_id`),
  CONSTRAINT `map_ibfk_1` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

/*Data for the table `map` */

insert  into `map`(`id`,`city`,`x`,`y`,`lat`,`lng`,`text`,`status_id`,`zoom`) values (1,'Москва',31.6,42.9,55.7448,37.6181,'Адрес',1,10);

/*Table structure for table `news` */

DROP TABLE IF EXISTS `news`;

CREATE TABLE `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `meta_title` varchar(255) DEFAULT NULL COMMENT 'meta_title',
  `meta_keywords` varchar(255) DEFAULT NULL COMMENT 'meta_keywords',
  `meta_description` varchar(255) DEFAULT NULL COMMENT 'meta_description',
  `data` text COMMENT 'Содержимое',
  `preview_img` varchar(255) DEFAULT NULL COMMENT 'Изображение',
  `date` date DEFAULT NULL COMMENT 'Дата',
  `title` varchar(255) DEFAULT NULL COMMENT 'Заголовок',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `prev_text` text COMMENT 'Текст предпросмотра',
  `slug` varchar(255) DEFAULT NULL COMMENT 'Адрес',
  PRIMARY KEY (`id`),
  KEY `status_id` (`status_id`),
  CONSTRAINT `news_ibfk_2` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=utf8;

