ntacts'),(22,5,'Задать вопрос?'),(23,5,'Тел: +87 (433) 399-99-22<br />\nWhatsUp: +87 (433) 399-99-22<br />\nViber:&nbsp;+87 (433) 399-99-22<br />\nSkype: amazingbali<br />\nMail: info@amazingbali.ru'),(24,8,'<strong><span style=\"font-size:22px;\">Маршрут тура</span></strong>'),(25,8,'Текст'),(26,8,'Текст'),(27,8,'Текст'),(28,8,'Текс1т'),(29,8,'Текст'),(30,4,'Новости'),(31,4,'Новости'),(32,9,'8 (800) 999 77 333'),(33,9,'For over 30 years we have been creating inspirational tailor-made holidays and are proud of our reputation'),(34,9,'Our Contacts'),(35,9,'1730 M Str., Suite 501, NW. Washington DC. 20036 U.S.A'),(36,9,'19008721'),(37,9,'19008197'),(38,9,'philip@gmail.com'),(39,9,'TravelTourism.org'),(40,9,'Follow Us'),(41,9,'Copyright © 2017 Travel & Tourism. All Rights Reserved.'),(43,3,'О нас'),(44,3,'Текст'),(45,3,'Текст');

/*Table structure for table `pages` */

DROP TABLE IF EXISTS `pages`;

CREATE TABLE `pages` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT 'Заголовок',
  `meta_keywords` varchar(255) DEFAULT NULL COMMENT 'meta_keywords',
  `meta_description` varchar(255) DEFAULT NULL COMMENT 'meta_description',
  `slug` int(11) DEFAULT NULL COMMENT 'Адрес',
  `data` text COMMENT 'Содержимое',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `lock` tinyint(1) DEFAULT NULL COMMENT 'Блокировка',
  `template` varchar(255) DEFAULT NULL COMMENT 'Шаблон',
  `image` varchar(255) DEFAULT NULL COMMENT 'Изображение',
  PRIMARY KEY (`id`),
  KEY `slug_id` (`slug`),
  KEY `status_id` (`status_id`),
  CONSTRAINT `pages_ibfk_2` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

