/*Table structure for table `partners` */

DROP TABLE IF EXISTS `partners`;

CREATE TABLE `partners` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL COMMENT 'Название',
  `imageFile` varchar(255) NOT NULL COMMENT 'Изображение',
  `status_id` tinyint(1) DEFAULT '1' COMMENT 'Статус',
  `sortOrder` int(11) DEFAULT NULL COMMENT 'Сортировка',
  `url` varchar(255) DEFAULT NULL COMMENT 'Ссылка',
  PRIMARY KEY (`id`),
  KEY `status_id` (`status_id`),
  CONSTRAINT `partners_ibfk_1` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8;

