/*Table structure for table `projects` */

DROP TABLE IF EXISTS `projects`;

CREATE TABLE `projects` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `meta_title` varchar(255) DEFAULT NULL COMMENT 'meta_title',
  `meta_keywords` varchar(255) DEFAULT NULL COMMENT 'meta_keywords',
  `meta_description` varchar(255) DEFAULT NULL COMMENT 'meta_description',
  `data` text COMMENT 'Описание',
  `preview_img` varchar(255) DEFAULT NULL COMMENT 'Изображение',
  `title` varchar(255) DEFAULT NULL COMMENT 'Заголовок',
  `slug_id` int(11) DEFAULT NULL COMMENT 'Адрес',
  `photo_1` varchar(255) DEFAULT NULL COMMENT 'Фотография №1',
  `photo_2` varchar(255) DEFAULT NULL COMMENT 'Фотография №2',
  `photo_3` varchar(255) DEFAULT NULL COMMENT 'Фотография №3',
  `photo_4` varchar(255) DEFAULT NULL COMMENT 'Фотография №4',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `type_id` int(11) DEFAULT NULL COMMENT 'Тип проекта',
  PRIMARY KEY (`id`),
  KEY `slug_id` (`slug_id`),
  KEY `status_id` (`status_id`),
  KEY `type_id` (`type_id`),
  CONSTRAINT `projects_ibfk_1` FOREIGN KEY (`slug_id`) REFERENCES `slugs` (`id`) ON DELETE CASCADE,
  CONSTRAINT `projects_ibfk_2` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`),
  CONSTRAINT `projects_ibfk_3` FOREIGN KEY (`type_id`) REFERENCES `projects_type` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

