/*Table structure for table `reviews` */

DROP TABLE IF EXISTS `reviews`;

CREATE TABLE `reviews` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT 'Заголовок',
  `meta_keyword` varchar(255) NOT NULL COMMENT 'meta_keyword',
  `meta_description` varchar(255) NOT NULL COMMENT 'meta_description',
  `description` varchar(255) NOT NULL COMMENT 'Описание',
  `short_text` text NOT NULL COMMENT 'Текст отзыва короткий',
  `long_text` text NOT NULL COMMENT 'Текст отзыва полный',
  `tour_img` varchar(255) DEFAULT NULL COMMENT 'Изображение тура',
  `client_img` varchar(255) DEFAULT NULL COMMENT 'Фотография клиента',
  `name` varchar(255) DEFAULT NULL COMMENT 'Имя',
  `position` varchar(255) DEFAULT NULL COMMENT 'Должность',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `tour_name` varchar(255) NOT NULL COMMENT 'Название тура',
  `cost` varchar(255) NOT NULL COMMENT 'Стоимость',
  `tour_id` int(11) DEFAULT NULL COMMENT 'Тур',
  `sort` int(11) DEFAULT NULL COMMENT 'Сортировка',
  PRIMARY KEY (`id`),
  KEY `status_id` (`status_id`),
  KEY `tour_id` (`tour_id`),
  CONSTRAINT `reviews_ibfk_1` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`),
  CONSTRAINT `reviews_ibfk_2` FOREIGN KEY (`tour_id`) REFERENCES `tours` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

