insert  into `reviews`(`id`,`title`,`meta_keyword`,`meta_description`,`description`,`short_text`,`long_text`,`tour_img`,`client_img`,`name`,`position`,`status_id`,`tour_name`,`cost`,`tour_id`,`sort`) values (1,'Отзыв о бали','','','тут какое то описание!!','Текст отзыва короткий<br />\r\nlkhuyfu','&quot;Текст отзыва полный 123&quot;','596a57959e29a.jpg','596a57dcf0ed7.jpg','Олег','Менджер',1,'На бали','2000 бачинских!!!',3,0),(2,NULL,'','','','','','596a5832e954c.jpg',NULL,'sdf','pdf',1,'ed','sdf',3,0),(4,NULL,'','','','Очень благодарны судьбе за то, что познакомились с лучшим, по нашему мнению, гидом!','Очень благодарны судьбе за то, что познакомились с лучшим, по нашему мнению, гидом! Ваян покорил своей честностью, пунктуальностью, отличным юмором и владением не просто хорошим русским, а еще и популярным русским сленгом Во время экскурсий постоянно хохотали \r\n Путешествовали впервые на Бали, но за три экскурсии, сумели охватить главные и самые популярные места острова! Мы очень довольны! Столько красоты увидеть за небольшой промежуток времени! Столько интересного узнать из жизни балийцев. Ваян Гриа большой молодец! \r\n Он для нас не только замечательный гид на Бали, но теперь и хороший друг!\r\nЖдем его с ответным визитом в нашу страну','596e122426f69.jpg','596e12243d594.jpg','Олег и Женя','Беларусь',1,'Танах Лот','50$',3,0);

