DROP TABLE IF EXISTS `tour_program`;

CREATE TABLE `tour_program` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `tour_id` int(11) DEFAULT NULL COMMENT 'Тур',
  `days` varchar(255) NOT NULL COMMENT 'Дни',
  `location` varchar(255) NOT NULL COMMENT 'Места',
  `description` text NOT NULL COMMENT 'Описание',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `sort` int(16) DEFAULT NULL COMMENT 'Сортировка',
  `day_hour` varchar(255) DEFAULT NULL COMMENT 'Дни - часы',
  PRIMARY KEY (`id`),
  KEY `tour_id` (`tour_id`),
  CONSTRAINT `tour_program_ibfk_1` FOREIGN KEY (`tour_id`) REFERENCES `tours` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=55 DEFAULT CHARSET=utf8;

/*Data for the table `tour_program` */

insert  into `tour_program`(`id`,`tour_id`,`days`,`location`,`description`,`status_id`,`sort`,`day_hour`) values (1,3,'0:00','Трансфер из Вашего отеля','Трансфер из вашего отеля до подножья горы Агунг',1,1,'часов'),(3,3,'2:00','Подножие горы Агунг','Прибытие к подножью горы Агунг',1,2,'часов'),(4,3,'4:30','Вершина священной горы острова Бали - Агунг','Прибытие на вершину Агунга',1,3,'часов'),(7,4,'16:00 ','Посещения пляжа Пандава и Дримленд','Первая остановка – пляж Пандава, где находятся вырезанные в скале статуи индуистских божеств. \r\nЗатем Вы посетите знаменитый пляж Дримлэнд.',1,2,'часов'),(8,9,'9:00','Встреча в отеле и дорога в храм Танах Лот','В 9:00 утра вас будет ждать около вашего отеля водитель, который отвезет вас на первое место нашей экскурсионной программы - к храму Танах Лот, который является одним из 9-ти священных балийских храмов. Примечателен он тем, что находится в море, на куске скалы, и приблизится к нему можно только во время отлива\r\n\r\n',1,1,'утра'),(9,10,'2:00','Встречаемся в лобби вашего отеля и отправляемся к подножию г. Батур','<div style=\"text-align: justify;\">Рускоговорящий водитель заберет вас из отеля и под покровом ночи вы отправитесь к подножию вулкана.&nbsp;</div>\n\n<div style=\"text-align: justify;\">Туристов, которые живут в районе <strong>Нуса Дум, Кута, Семиньяк, Чангу</strong> - мы забираем в 1:30 утра. <strong>Улувату и Букит </strong>- 1:00. В районе <strong>Санур</strong> - 2:00. Туристов живущих в <strong>Убуде</strong> в 2:30 утра.</div>\n\n<div style=\"text-align: justify;\">Перед восхождением гид проведёт брифинг и инструктаж, выдаст фонарики. Одевайтесь потеплее: на вершине довольно прохладно и ветер. Ваше восхождение начинается! Трасса не сложная, доступна человеку с минимальной физической подготовкой, однако мы просим вас прислушиваться к рекомендациям гида, чтобы не заблудиться в темноте или не пораниться.</div>\n',1,NULL,'часа утра'),(10,10,'4:00','Подножие вулкана, начинаем подъем','К вершине вы доберётесь как раз к началу рассвета. Если вы хотите поснимать, у вас будет время установить оборудование и выбрать лучшие ракурсы. Завораживающий, восхитительный рассвет стоит любых усилий. Если вам повезёт, то поднимется туман и волшебство этого места превзойдёт все ожидания. Вы окажетесь нигде посреди ничего — это непередаваемое ощущение.',1,NULL,'часов'),(11,10,'6:00','Встречаем рассвет на вершине вулкана Батур','Встречаем рассвет. Гид приготовит необычный завтрак на вершине вулкана: яйца на горячем водяном пару, горячий чай или кофе. После завтрака медленно спустимся, любуясь окрестностями. По желанию, у подножия вулкана вы можете искупаться в горячих источниках, выпить свежевыжатого сока и насладиться вкусным обедом с видом на Батур.',1,NULL,'часов'),(13,4,'18:00 ','Посещения храма Улувату','Его еще называют \"храмом на краю земли\", так как он возвышается на 100-метровой скале. На территории храма Улувату находится Лес обезьян, где Вы также сможете прогуляться и покормить их фруктами. \r\nИз храма открывается невероятный вид на скалы и океан.  Здесь же во время заката Вы посмотрите знаменитый огненный танец Кечак. Это один из древнейших балийских ритуальных танцев. \r\n\r\n',1,3,'часов'),(14,4,'15:00','Встреча с гидом в лобби вашего отеля','Экскурсия начинается с того, что в холле вашего отеля после обеда, в районе 15:00 часов, вас будет ждать русскоговорящий гид, с которым вы отправитесь на пляж Пандва, о котором экскурсовод расскажет вам много интересных фактов. ',1,1,'часов'),(15,5,'9:00','Встреча с гидом в лобби Вашего отеля','Утром, в холле вашего отеля, вас будет ждать ваш гид, который проведет вас по самым таинственным уголочкам восточной части Острова Богов. ',1,1,'часов'),(16,5,'11:00','Храм Гоа Лавах или Дворец Летучей Мыши','Первым местом для посещения станет  храм летучих мышей Гоа Лавах, где вы увидите как с визгом и хлопаньем крыльев на закате в небо вырываются тысячи летучих мышей. ',1,2,'часов'),(17,5,'13:00','Шоколадная фабрика','После Дворца Летучей Мыши вы отправитесь прямиком на шоколадную фабрику Чарли. Здесь повсюду вкуснейший шоколад и сладости,  сказочные декорации среди тропических лесов и праздничная атмосфера',1,3,'часов'),(18,5,'14:30 ','Водный дворец Тигра Гангга','Далее вы посетите Водный дворец Тирта Ганга, который представляет собой прекрасный парк с большим количеством фонтанов и купален. Здесь вы в полной мере прочувствуете тонкости культуры Хинду',1,4,'часов'),(19,5,'17:00','Водный дворец Таман Уджунг','Второй дворец, который вы посетите в рамках этой экскурсии,  был так же построен королём Анак Агунг Англурах Кетут и называется Tаман Уджунг. По преданию король лично принимал участие в строительстве этого дворца наряду с простыми рабочими, строительство и проектировка было его хобби. ',1,5,'часов'),(20,20,'9:00','Встреча с гидом в лобби Вашего отеля','В 9:00 утра вас встретит экскурсовод в лобби вашей гостиницы, с которым вы направитесь к первому месту в вашей экскурсионной программы - Водному дворцу Тирта Ганга.\r\n',1,1,'часов'),(21,20,'11:00','Дворец Тирта Ганга','Посетить дворец Тирта Ганга состоит каждому: этот монументальный дворец состоит из 3-х небольших комплексов, десятка статуй, мостиков \r\n',1,2,'часов'),(22,20,'13:00','Посещение храма Пура Лямпуянг','Этот храм вляется одним из самых древних комплексов на Бали. Он включает в себя 10 храмов, которые находятся в восточной части Бали на склоне Лямпуянг. Как и Бесаких, Лемпуянг состоит их семи уровней и главный храм (Luhur Lempuyang) возвышается над остальными. Чтобы к нему подняться, необходимо будет миновать 1700 ступенек.',1,3,'часов'),(23,20,'14:00','Дворец Таман Уджунг','В центр нашего путешествия мы поставили превосходный дворец на воде - Таман Уджунг Парк Уджунг изобилует великолепной флорой и фауной, он достаточно красивый и живописный.  ',1,4,'часов'),(24,20,'15:00 ','Храм Летучей Мыши или Гоа Лавах','Следующим местом для посещения станет  Храм Летучей Мыши или  Гоа Лавах, где вы увидите как с визгом и хлопаньем крыльев в небо вырываются тысячи летучих мышей. ',1,5,'часов'),(25,20,'17:00','Шоколадная фабрика Чарли','После Дворца Летучей Мыши вы отправитесь прямиком на шоколадную фабрику Чарли. Здесь повсюду вкуснейший шоколад и сладости,  сказочные декорации среди тропических лесов и праздничная атмосфера',1,5,'часов'),(27,7,'9:00','Трансфер из отеля до пляжа Ловина','В 9:00 утра вас будет ждать около вашего отеля водитель, который отвезет вас на первое место нашей экскурсионной программы - на пляж Ловина с черным вулканическим песком. Вдоль линии берега расположено шесть деревень, в каждой из которых много ферм и различных плантаций. \r\n',1,1,'утра'),(28,7,'11:00','Катание на традиционной балийской лодке','В лучах утреннего солнца капитан небольшого балийского рыбацкого судна “джукунг” отправится вместе с вами прямо в Тихий океан, по маршруту следования дельфинов. Так что вполне вероятно, что вы встретите этих удивительных морских обитателей \r\n',1,2,'часов'),(29,7,'13:00','Водопад Секумкул','Следующим пунктом нашей программы будет комплекс из 7 водопадов - Секумпул, священное место для каждого жителя Бали. Высота самого крупного водопада составляет 80 метров. Покидая это природное чудо, вы проездом сможете полюбоваться на горные озера Буян и Тамблинган. \r\n',1,3,'часов'),(30,7,'15:00','Едем в Храм Улан Дану','В середине вашего пути вам представится возможность посетить храм  Пура Улун Дану, который находится на священном озере Бератан в окружении густых тропических зарослей.',1,4,'часов'),(31,7,'16:30','Заброшенный отель','После небольшого обеденного перерыва вы направитесь в таинственное и даже мистическое место - заброшенный отель P. I. Bedugul Taman Rekreasi Hotel & Resort. Это огромное здание отличается изяществом архитектуры, роскошью и великолепием внешнего и внутреннего оформления, а с террасы открывается великолепный вид на горы. \r\n',1,5,'часов'),(32,7,'19:00','Возвращаемся в отель','К 18:00 часам вечера, как раз на заходе солнца, наша экскурсия подойдет к концу. Вы можете полюбоваться невероятно красивым закатом, после чего водитель отвезет вас обратно в гостиницу. ',1,6,'часов'),(33,8,'9:00','Дорога из отеля до водопада Гит-Гит','Посещение водопада Гит-Гит, который находится в живописном месте между плантациями гвоздики и рисовыми террасами неподалеку от города Сингараджа. Высотой более 40 метров, состоящий из нескольких уровней, водопад Гит-Гит считается одним из самых высоких на острове.\r\n',1,NULL,'утра '),(34,8,'14:00','Посещение водопада Мундук','Последний водопад в нашей экскурсионной программе - Мундук, высота которого достигает 26 метров. Расположен он, как и большинство других водопадов, в самом сердце джунглей, и чтобы добраться до него, вам нужно будет взобраться на высоту 300 метров, к тому же в нем вы можете искупаться. После посещения последнего водопада гид отвезет Вас назад в отель.',1,NULL,'часов'),(35,9,'12:00','Водопад Гит-Гит','После этого удивительного храма вы отправитесь к водопаду Гит-Гит. Пожалуй, это один из самых красивых водопадов на острове. Свое название он получил благодаря своему внешнему виду: он имеет два струи, которые каскадом спускаются со скалы. \r\n',1,2,'часов'),(36,9,'14:00','Дорога в храм Улан Дану','В середине пути вам представится возможность посетить храм Пура Улун Дану, который находится на священном озере Бератан в окружении густых тропических зарослей. ',1,3,'часов'),(37,9,'16:00','Заброшенный отель','После небольшого обеденного перерыва вы направитесь в таинственное и даже мистическое место - заброшенный отель P. I. Bedugul Taman Rekreasi Hotel & Resort. Это огромное здание отличается изяществом архитектуры, роскошью и великолепием внешнего и внутреннего оформления, а с террасы открывается великолепный вид \r\n',1,4,'часов'),(38,9,'18:00','Возвращаемся в отель','Полюбовавшись закатом солнца с террасы заброшенного отеля, вас отвезут в вашу гостиницу, где вы можете отдохнуть после насыщенного экскурсионного дня по северо-западной части острова Бали\r\n',1,5,'часов'),(39,12,'1','Лабуанбаджо,  остров Ринча и Комодо','7:30 вылет с бали \r\n9:00 Лабуанбаджо\r\nПервый день посмотри остров ринча, комодо и ночуем на корабле',1,NULL,'День'),(40,12,'2','Розовый пляж, Снорклинг, Манты, Остров Бидадари','Второй ден посомтрим пляж с розовым песком там покупаемся и по снорклим, потом поедем на манта поинт -купаться с мантами, после остров Бидадари и домой в порт\r\nв 16:00 летим на бали',1,NULL,'День '),(41,17,'9:00','Встеча в лобби отеля и дорога в Туламбен','Встреча в отеле + Помимо дайвинга вас ожидает увлекательное путешествие на восток острова. Вы увидите живописные рисовые террасы, пляж с чёрным песком, смену климатических поясов. Персональное внимание к вам и отличное настроение, много фотографий, захватывающих видов, красоты и первобытность востока Бали не оставят вас равнодушным!',1,NULL,'часов'),(42,17,'11:00','Подготовка к погружению и дайвинг в Туламбене','Погружение. Возвращение в отель.',1,NULL,'часов'),(43,8,'16:00','Водопад Алинг-Алинг','Последний пункт в нашей экскурсии - водопад Алинг-Алинг, высота которого составляет 35 метров. Знаменит он тем, что вокруг него находится много других маленьких водопадов. Здесь можно искупаться в лагуне, съехать вниз по водопаду и, для самых смелых, - прыгнуть со скалы в лагуну!\r\n',1,NULL,'часов'),(44,21,'1','Нуса Пенида','<br />\n<span style=\"font-size:20px;\">​​​​<strong>&mdash;&nbsp;6:30</strong></span> Трансфер&nbsp;заберёт&nbsp;вас из отеля на Бали и доставит в порт;<br />\n<strong><span style=\"font-size:20px;\">&mdash;&nbsp;9:15</span><span style=\"font-size:18px;\"> </span></strong>Прибытие в порт&nbsp;острова Нуса Пенида. Заселение в отель и завтрак;<br />\n<span style=\"font-size:20px;\"><strong>&mdash;&nbsp;11:00</strong></span> Отправление из отеля на экскурсию: восточные пляжи Нуса Пенида, лучшие панорамные виды острова с прибрежных скал, восхождение в горы и спуск по скалистой тропе к белоснежному&nbsp;пустынному&nbsp;пляжу Atuh;<br />\n<span style=\"font-size:20px;\"><strong>&mdash;&nbsp;16:00-17:00</strong> </span>Возвращение в отель, ужин, отдых и расслабление после насыщенного дня. в отель и завтрак; отправление из отеля на экскурсию: восточные пляжи Нуса Пенида, лучшие панорамные виды острова с прибрежных скал, восхождение в горы и спуск по скалистой тропе к белоснежному пустынному пляжу Atuh; возвращение в отель, ужин, отдых и расслабление после насыщенного дня.',1,NULL,'День'),(45,21,'2','Нуса Пенида','<br />\n<strong><span style=\"font-size:20px;\">&mdash;&nbsp;07:00</span></strong> Ура, день снорклинга!&nbsp;Вы прослушаете&nbsp;брифинг для комфортного нахождения в океане и вперед: нырять!&nbsp;Целый день вы ездите&nbsp;по самым красивым бухтам острова и плаваете на лучших спотах;<br />\n<span style=\"font-size:20px;\"><strong>&mdash;&nbsp;13:30</strong></span> Обед и посещение природного&nbsp;бассейна на пляже Angel&#39;s Billabong Beach и Broken Beach.<br />\n<strong><span style=\"font-size:20px;\">&mdash;&nbsp;15:30</span></strong> Отправление из порта острова Нуса Пенида;<br />\n<span style=\"font-size:20px;\"><strong>&mdash;&nbsp;17:00</strong></span> Прибытие на остров Бали, трансфер в отель.',1,NULL,'день'),(46,14,'1 ','Трансфер из отеля в порт Гилиманук','Вечером, в 19:00 часов, когда день будет приближаться к концу и солнце прятаться за горизонт, за вами заедет гид и отвезет вас в порт Гилиманук,  откуда отплывает паром на остров Ява. ',1,1,'день'),(47,14,'1','Переправа к подножью вулкана','Уже через час, в 20:00 вы будете на пароме в порту Гилиманук, который переправит вас из острова Бали к восточному побережью острова Ява, где находится вулкан.',1,2,'день'),(49,8,'18:00 ','Трансфер до вашего отеля','После завершения экскурсии, в конце насыщенного дня, трансфер доставит вас по месту жительства в ваш отель., где вы сможете расслабиться и проводить сансет, вспоминая этот яркий день!',1,NULL,'часов'),(50,4,'20:00','Ужин на пляже Джимбаран и трансфер в отель','В конце путешествия гид может отвезти Вас, если вы проголодаетесь и захотите поужинать на берегу океана, на пляж Джимбаран, где находятся знаменитые рыбные рестораны прямо на берегу океана.  После этого Вас отвезут домой.',1,4,'часов'),(51,14,'1','Стоянка и отдых перед подъемом','В 22:00 вы уже будете в базовом лагере, где все желающие смогут передохнуть после дороги, подкрепиться и набраться сил перед предстоящим подъемом на вершину вулкана \r\n',1,3,'день'),(52,14,'2','Начало восхождения','В 2:00 ночи гид поведет вас прямо к вершине вулкана, откуда открывается фантастически-прекрасная картина! В 6:00 утра вы уже будете на вершине созерцать восход солнца, любуясь местными пейзажами. ',1,4,'день'),(53,14,'2','Спуск к базовому лагерю','В 8:00 утра, встретив рассвет и вдоволь налюбовавшись \"марсианскими\" пейзажами,  вам предстоит спуск обратно. По желанию, у подножия вулкана вы можете искупаться в горячих источниках, выпить стаканчик сока и пообедать с видом на Батур',1,5,'день'),(54,14,'2','Трансфер на Бали и в отель','В 15:00 вас будет ожить паром, который доставит вас на остров Бали, к тому самому порту, откуда вы приплыли днем ранее - Гилиманук. Там вас будет ожидать машина с водителем, который доставит вас в ваш отель',1,6,'день');

/*Table structure for table `tours` */

DROP TABLE IF EXISTS `tours`;

CREATE TABLE `tours` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `meta_title` varchar(255) NOT NULL,
  `meta_keywords` varchar(255) NOT NULL,
  `meta_description` varchar(255) NOT NULL,
  `title` varchar(255) NOT NULL COMMENT 'Название',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `type_id` int(11) DEFAULT NULL COMMENT 'Категория',
  `sort` int(10) DEFAULT NULL COMMENT 'Сортировка',
  `stars` int(1) DEFAULT NULL COMMENT 'Звезды',
  `duration` varchar(255) NOT NULL COMMENT 'Продолжительность',
  `short_description` varchar(255) NOT NULL COMMENT 'Короткое описание',
  `long_description` text NOT NULL COMMENT 'Полное описание',
  `places` varchar(255) NOT NULL COMMENT 'Мест доступно',
  `destination_id` int(11) DEFAULT NULL COMMENT 'Пункт назначения',
  `new_price` decimal(10,0) DEFAULT NULL COMMENT 'Новая цена',
  `old_price` decimal(10,0) DEFAULT NULL COMMENT 'Старая цена',
  `tour_img` varchar(255) DEFAULT NULL COMMENT 'Изображение тура',
  `short_program` varchar(255) NOT NULL COMMENT 'Программа',
  `slug` varchar(255) DEFAULT NULL COMMENT 'Ссылка',
  `popular` tinyint(1) DEFAULT NULL COMMENT 'Популярный',
  `hit` tinyint(1) NOT NULL COMMENT 'Хит',
  `sale` tinyint(1) NOT NULL COMMENT 'Распродажа',
  `duration_type` int(1) NOT NULL COMMENT 'Тип продолжительности',
  `duration_time` int(11) NOT NULL COMMENT 'Продолжительность',
  `program_description` text NOT NULL COMMENT 'Описание программы',
  PRIMARY KEY (`id`),
  KEY `status_id` (`status_id`),
  KEY `tours_ibfk_2` (`type_id`),
  KEY `destination_id` (`destination_id`),
  CONSTRAINT `tours_ibfk_1` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`),
  CONSTRAINT `tours_ibfk_2` FOREIGN KEY (`type_id`) REFERENCES `tours_type` (`id`),
  CONSTRAINT `tours_ibfk_3` FOREIGN KEY (`destination_id`) REFERENCES `destination` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8;

/*Data for the table `tours` */

insert  into `tours`(`id`,`meta_title`,`meta_keywords`,`meta_description`,`title`,`status_id`,`type_id`,`sort`,`stars`,`duration`,`short_description`,`long_description`,`places`,`destination_id`,`new_price`,`old_price`,`tour_img`,`short_program`,`slug`,`popular`,`hit`,`sale`,`duration_type`,`duration_time`,`program_description`) values (3,'Восхождение на вулкан Агунг - самую высокую гору на острове Бали. Описание, фото, цены','Восхождение на Агунг, туры на Агунг, трекинг агунг, подъем на агунг бали','Восхождение на священную гору Бали - Агунг, посещение храма Бесаких. Экскурсия на Бали гидом для взрослых и детей. Трансфер от отеля до места сбора ','Восхождение на Агунг',1,1,2,5,'6 часов','Не каждому под силу совершить восхождение на вершину самой священной горы Бали - Агунг','Полное описание','до 5 чел.',4,40,1900,'596b914fc3bb4.jpg','Подъем, рассвет, спуск','zagolovok-2',1,0,0,1,6,'<h2 style=\"text-align: center;\">Восхождение на священную гору Бали - вулкан Агунг&nbsp;</h2>\r\n\r\n<p style=\"text-align: justify;\">Гора Агунг, расположенная в восточной части острова, не только самая высокая гора на индонезийском острове Бали (высота ее составляет 3142 м), но и самая почитаемая и даже священная среди местного населения. Не каждый осмелится совершить восхождение на вершину этого вулкана, но тот, кто все-таки решится на этот подвиг, в конце пути сможет созерцать фантастический панорамный вид на Бали и близлежащие острова (Ява, Нуса Пенида и Лембоган). На самой вершине Агунга вы почувствуете себя птицей, парящей в облаках.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Перед восхождением на вершину горы вас ждет молитвенный обряд, который пройдет в храме Бесаких, который балийцы особо почитают. По поверью, обряд совершается для того, чтобы духи не мешали всем желающим посетить гору. Восхождение на Агунг - достаточно серьезное испытание, потому требуется хорошая физическая подготовка. Около полуночи вас из отеля заберет гид, сам поход начинается около часу-двух ночи и продолится 3-3,5 часа. Добравшись на вершину Агунга вы встретите на пике горы первые лучи восходящего солнца!&nbsp;</p>\r\n'),(4,'Экскурсии с гидом в храм Улувату на острове Бали, танец Кечак и пляжи с белым песком','Храм Улувату, танец Кечак, Улувату Бали, религия Бали,  достопримечательности Бали, ','Предлагаем недорого туры в храм Улувату (Индонезия), где вам покажут танец Кечак, после чего вы отправитесь на чистейшие пляжи Бали с белым песком. Описание, фото, цены туров на остров Бали. ','Улувату и танец Кечак',1,4,2,5,'6','Окунись в амтосферу Балиских духов в Храме Улувату','','1900',1,60,1900,'596ba36241b0e.jpg','Пандава, Дримленд, Улувату','tur2',1,0,0,0,0,'<h2 style=\"text-align: center;\">Прогулка по пляжам Бали и знакомство с храмом Улувату</h2>\r\n\r\n<p style=\"text-align: justify;\">Эта увлекательная экскурсия по южному&nbsp;Бали познакомит вас с самыми красивыми пляжами южной части острова, а также с важнейшим храмовым комплексом - Улувату, где вы увидите национальный танец, который больше похож на таинственное представление - танец Кечак.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Начинается экскурсия после обеда: в 15:00 вы с гидом посетите знаменитый пляж Паданг-Паданг, где проходили съемки фильма &ldquo;Ешь, молись, люби&rdquo; в главных ролях с голливудской актрисой Джулией Робертс. После того, как вы вдоволь поплаваете и позагораете, вы отправитесь на другой пляж, не менее красивый, чем предыдущий - Сулубан, где открывается красивый вид на скалы Бали.</p>\r\n\r\n<p style=\"text-align: justify;\">После посещения пляжей вы направитесь в храмовый комплекс Улувату, который находится на самой дальней точке полуострова. Здесь перед вами откроется фантастическая панорама побережья Индийского океана и тропических лесов. На заходе солнца вам покажут необычное зрелище: балийский танец Кечак, который больше напоминает спектакль или музыкальную драму. Если вы проголодаетесь, в конце экскурсионного дня вас ждет ужин из&nbsp;морепродуктов в ресторане на пляже Джимбаран.&nbsp; &nbsp;</p>\r\n'),(5,'Экскурсии по Востоку Бали. Архитектурные достопримечательности, быт и культура. Описание туров, фото, цены','экскурсии на бали, восток бали, восточный бали, бали экскурсии, экскурсии бали, экскурсии на бали цены, стоимость экскурсий на бали, остров бали экскурсии, экскурсии бали индонезия, экскурсии на острове бали, экскурсии по индонезии, отдых на бали экскурси','Экскурсии по восточному Бали с русскоязычным гидом: архитектурные памятники, религия и культура балийцев. Выбрать экскурсионный тур на Бали по низкой цене! Описание экскурсий, фото, стоимость.','Водные дворцы востока',1,4,2,5,'60','Если вы хотите почувствовать магию Бали, познакомиться с основами культуры Хинду, тогда добро пожаловать на восток Бали!','Полное описание','1900',1,70,1900,'5968cdd609397.jpg','Гоа Лавах, Тигра Гарга, ТАман Уджун','zagolovok-4',1,0,0,0,0,'<p>Восток Бали достаточно необычен и разнообразен даже для Индонезии, и все красоты этой местности вряд ли можно объехать за один день. Но мы составили для вас несколько экскурсионных программ, одну из которых хотим предложить: исследование княжества Карангасем.&nbsp;</p>\r\n\r\n<p>Первый пункт вашего маршрута - храм летучих мышей Гоа Лавах. Эта пещера стала пристанищем сотен, а то и тысяч фруктовых летучих мышей, и, как гласят легенды, исполинских размеров дракона-змея Басуки.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Следующее место, которое вы посетите в рамках нашего тура - шоколадная фабрика Чарли. В этом месте все напоминает сказочный мир фэнтези: в кукольных домиках могли бы жить эльфы, на качелях между пальмами могли бы резвиться гномы, а из-за ярких декораций то и кажется вынырнут миниатюрные пони. Конечно же, здесь много, очень много вкусного шоколада!&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Побывав в гостях у сказки, вы вместе с гидом направитесь к Водному дворцу Тирта Ганга (в переводе означает &ldquo;священная река Ганг&rdquo;), возведенный в 1946 году королем Анак Агунг Англурах Кетут. В этом месте сосредоточено большое количество фонтанов, купален и и других природных и искусственных водоемов разной величины. Неподалеку от Тирта Ганга находится другой, не менее изящный дворец с множеством фонтанов, озер с золотыми карпами, утопающий&nbsp;в яркой зелени джунглей - Таман Уджунг. Согласно преданиям&nbsp;сам король, очарованный роскошью Версаля, принимал участие в возведении этого дворца, которое велось до 1948 года. Находясь на территории дворца Таман Уджунг вы с головой окунетесь в атмосферу древнего Королевства Маджапахит.</p>\r\n\r\n<p style=\"text-align: justify;\">В конце насыщенного экскурсионного дня вас ждет отдых на знаменитом пляже с белым песком - White Sand Beach. Купайтесь, загорайте, отдыхайте и наслаждайтесь романтическими вечерними пейзажами побережья Индийского океана. Проголодались? Тогда предлагаем вам полакомиться морепродуктами в одном из пляжных кафе.&nbsp;</p>\r\n'),(6,'Meta Title','Meta Keywords','Meta Description','Центр о. Бали ',1,3,2,5,'60','Короткое описание','Полное описание','1900',1,800,1900,'5968e718dc60d.jpg','Программа','zagolovok-5',1,0,0,0,0,'00:00:00'),(7,'Тур на север Бали: дельфины, водопад Секумпул, храм Улан Дану, заброшенный отель','север бали, пляжи севера бали, север острова бали, тур на Север Бали, тур на Север Бали цена, экскурсия на Север Бали, Север Бали описание, дельфины на Бали, тур к дельфинам Бали, что посмотреть на Севере Бали, Север Бали достопримечательности, водопад Се','Предлагаем для взрослых и детей увлекательный тур на север Бали по доступным ценам. Вы увидите настоящих дельфинов, водопад Секумпул, храм Улан Дану, заброшенный отель. Подробное описание тура, красочные фото, бронь тура прямо у нас на сайте!  ','Север острова Бали',1,1,2,5,'12','Непроходимые тропики, пляжи с вулканическим песком, редкие животные, храмы, таинственные места и многое другое вас ждет на севере Бали','Полное описание','5',1,800,1900,'59663a6a8a829.jpg','Дельфины, храмы, водопад','zagolovok-6',1,1,0,1,12,'<h2 style=\"text-align: center;\">Приглашаем вас насладиться поразительным и разнообразным миром северного Бали</h2>\r\n\r\n<p style=\"text-align: justify;\">Тур по северу Бали достаточно долгий и насыщенный: за один день вы побываете во всех уголках северной части острова и увидите все достопримечательности, которыми гордится северный Бали. Для вас эта экскурсия станет настоящим приключением!&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Прямо из отеля вас привезут на пляж Ловина, который знаменит своим черным вулканическим песком. Это относительно малолюдный тихий пляж, из-за отсутствия волн здесь достаточно хорошо развит снорклинг и дайвинг. Вдоль береговой линии расположились 6 небольших деревушек с множеством ферм и плантаций. Фауна и флора здешних мест отличается от других районов острова благодаря плодородной вулканической почве.</p>\r\n\r\n<p style=\"text-align: justify;\">После небольшого экскурса вас ждет морская прогулка с дельфинами. На небольшой балийской национальной рыбацкой лодке, которая называется &ldquo;джукунг&rdquo; вас провезут по местам, где водятся дельфины. Если вам повезет, то вы можете не только увидеть этих умных и дружелюбных животных, а и сделать несколько снимков на память.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Затем, минуя ущелья по узким горным тропам, вы воочию увидите настоящее чудо - водопад Секумпул. В этом комплексе 7 водопадов, самый высокий - 80-метровый водопад . Еще совсем недавно это место было закрыто для посещения и только в 2010 году его открыли для туристических экскурсий. По дороге к следующей &ldquo;северной гордости&rdquo; вы будете проезжать озера Буян и Тамблинган, которые раскинулись прямо в кратере потухшего вулкана Батур.</p>\r\n\r\n<p style=\"text-align: justify;\">Природные красоты острова Бали - это далеко не все, что мы для вас приготовили. После обеда вы попадете в храм Пура Улун Дану, находящийся рядом со священным озером Бератан и окруженный тропическими лесами. Этот храм был построен в 1663 году королем Менгви и посвящен богини воды - Деви Дану, которая, по преданиям балийцев, дарует благополучие и процветание.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Любителям острых ощущений, загадок и тайн, в конце нашего тура предоставляется возможность &ldquo;пощекотать&rdquo; нервы, осторожно поднимаясь по истертым временем ступеням заброшенного отеля P. I. Bedugul Taman Rekreasi Hotel &amp; Resort. Помимо своих впечатляющих размеров, этот отель поразит вас своим изяществом и роскошью, а какие только великолепные виды открываются с его террасы! Кстати, заканчивая наше путешествие, с этой террасы вы можете насладиться заходящим в горы красным диском солнца. После этого вас ждет обратный трансфер в гостиницу и уже к 20:00 часам вы будете у себя в номере.&nbsp;</p>\r\n'),(8,'Самые красивые водопады Бали: Гит-Гит, Мундук, Алинг-Алинг','водопады Бали, экскурсии на Бали, экскурсии на бали цены, экскурсии бали индонезия, экскурсии на острове бали,  отдых на бали, ','Экскурсии на Бали по низким ценам. Широкий выбор увлекательных экскурсий на острове Бали по водопадам, джунглям и другим природным достопримечательностям. Цены, описание, фото','Водопады острова Бали',1,1,2,5,'12','Самые красивые водопады Индонезии на Острое Богов! ','Полное описание','5',1,800,1900,'598c6fb7dbf74.jpg','Программа','zagolovok-3',1,0,0,1,12,'<h2 style=\"text-align: center;\">Экскурсии по невероятным водопадам острова Бали</h2>\r\n\r\n<p style=\"text-align: justify;\">Красоты 40-метрового водопада Гит-Гит не оставят вас равнодушными, как и окружающая его флора и фауна: склоны гор, рисовые поля с дымкой на склонах, стаи птиц дивной красоты. Станет жарко - спуститесь 350 ступеней вниз и окунитесь в прохладную воду Индийского океана&hellip;</p>\r\n\r\n<p>После этой прогулки вы отправитесь к неизвестному, но очень красивому водопаду Мундук, который срывается бурлящим потоком с высоты 15 метров. Здесь вы сможете побыть наедине с собой и с природой.</p>\r\n\r\n<p>Последним в списке, но не менее интересным и захватывающим дух, будет для вас посещение водопада Алинг-Алинг, высота которого составляет целых 35 метров! Вокруг этого &ldquo;чуда природы&rdquo; находятся с десяток мелких водопадов, в которых можно приятно провести время, искупаться, заняться слайдингом и даже&nbsp;прыгнуть со скалы в лагуну!&nbsp;</p>\r\n\r\n<p>Для тех, кто жаждет приключений, советуем еще посетить рисовые террасы Джатилувих площадью 300 га, расположенные на высоте 805 м над уровнем моря на горе Батукару, &nbsp;где вы можете увидеть систему ирригации полей Субак. В 2012 году эта местность была была признана Всемирным наследием ЮНЕСКО.</p>\r\n'),(9,'Тур по острову Бали: храм Танах Лот, священный водопад Гит-Гит, озеро Бератан, храм Улун Дану, клубничные плантации и заброшенный отель.','север бали, северо-запад Бали, тур на северо-запад Бали, север острова бали, тур на север Бали, тур на север Бали цена, экскурсия на север Бали, север Бали описание,  что посмотреть на севере Бали, север Бали достопримечательности, водопад Гит-Гит, храм У','Предлагаем для взрослых и детей увлекательный тур по северной части острова Бали по доступным ценам. Вы увидите священный водопад Гит-Гит, озеро Бератан, храм Улан Дану, заброшенный отель, клубничные плантации. Подробное описание тура, фото, бронь прямо у','Северо-запад о. Бали',1,4,2,5,'10','На северо-западной части острова Бали вас ждут священные водопады и храмы, горные озера и фруктовые плантации и другие, не менее интересные места','Полное описание','5',1,60,1900,'5977c1f6442d6.jpg','Танах Лот, Гит-Гит, Улан Дану','zagolovok-8',0,0,0,1,0,'<h2 style=\"text-align: center;\">Экскурсия по северо-западной части острова Бали&nbsp;</h2>\r\n\r\n<p style=\"text-align: justify;\">Экскурсионная программа по северо-западной части Бали яркая и многогранная: за один день вы побываете во всех уголках северо-запада острова и увидите самые интересные его достопримечательности. Для вас этот тур станет незабываемым! &nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Прямо из отеля вас доставят к индуистскому храму Танах Лот, который является одним из 9-ти священных храмов на Острове Богов. Среди туристов он получил свою известность благодаря своему необычному расположению: находится он прямо в море, на куске скалы, и приблизится к нему можно только во время отлива. Это &ldquo;балийское чудо&rdquo; находится под охраной ЮНЕСКО. Сам храмовый комплекс встречает гостей монументальными воротами, ведущих к храмам и морю. Внутрь разрешается заходить только индуистам. Вас могут заинтересовать 2 смотровые площадки, которые находится неподалеку от Танах Лот. С них открывается великолепная панорама, которую вы просто обязаны увидеть и запечатлеть на фото.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">После храмового комплекса Танах Лот вы отправитесь к водопаду Гит-Гит - одному из самых высоких (более 40 метров) и полноводных водопадов на Бали. Такое название он получил благодаря своему внешнему виду: он имеет две&nbsp;струи, которые каскадом спускаются со скалы. У подножья водопада находится небольшое озеро, в котором можно искупаться (если вы не боитесь холодной воды).<br />\r\nСледующим обязательным пунктом для посещения является храм Пура Улун Дану, находящийся рядом со священным озером Бератан и окруженный тропическими лесами.</p>\r\n\r\n<p style=\"text-align: justify;\">И напоследок вас ждет немного жутковатое, на первый взгляд, место - заброшенный отель &nbsp;P. I. Bedugul Taman Rekreasi Hotel &amp; Resort. Помимо своих впечатляющих размеров, это здание поражает своим изяществом и роскошью, а какие только великолепные виды открываются с его террасы! Кстати с этой террасы вы можете насладиться заходящим в горы красным диском солнца. Ваше путешествие закончится около 18:00 вечера, в гостиницу вы вернетесь приблизительно к 19:00 часам.</p>\r\n'),(10,'Meta Title','Meta Keywords','Meta Description','Восхождение на Батур',1,1,2,5,'6 часов','Выбрав этот тур вы увидите один из самых красивых видов на горы на острове Бали.','','до 10 человек',4,50,60,'5977c1b30b8f5.jpg','Подъем на вулкан','zagolovok-7',0,0,0,0,6,''),(11,'Meta Title','Meta Keywords','Meta Description','Центра о. Бали',1,4,2,5,'60','Короткое описание','Полное описание','1900',1,800,1900,'596ba683e6790.jpg','Программа','zagolovok-9',0,0,0,0,0,'00:00:00'),(12,'Meta Title','Meta Keywords','Meta Description','Острова Комодо',1,1,2,5,'2','Острова Комодо','Полное описание','1900',1,800,1900,'59821ddc2045d.jpg','Программа','zagolovok-10',0,0,0,0,48,'00:00:00'),(13,'Meta Title','Meta Keywords','Meta Description','Папуа Раджа',1,1,2,5,'60','Короткое описание','Полное описание','1900',1,800,1900,'5977c28aced55.jpg','Программа','zagolovok-11',0,0,0,0,0,'00:00:00'),(14,'Восхождение на вулкан Инджен, экскурсии на острове Ява, Индонезия','туры на Инджен, восхождение на Инджен, тур на Инджен цена, экскурсия на вулкан Инджен, туры на Яву, экскурсия на остров Ява','Экскурсия на действующий вулкан Инджин на острове Ява - захватывающее дух приключение, в которое вы можете отправиться вместе с нами! Увидьте уникальные природные явления - кислотное озеро, действующий вулкан и сияние над разломом своими глазами! ','Восхождение на Инджен',1,1,2,5,'1 сутки','Фантастические пейзажи острова Ява: разлом действующего вулкана, кислотное озеро и “северное сияние” прямо на экваторе планеты!','Полное описание','от 2 человек',1,120,1900,'5968ecd38029e.jpg','Остров Ява, Иджен','zagolovok-12',0,0,0,1,24,'<p style=\"text-align: justify;\">Вы получите незабываемые впечатления, побывав около действующего вулкана Инджен, который расположен в восточной части острова Ява. Спустившись на 200 метров по ярко-красным скалам прямо в кратер вулкана, вы сможете увидеть уникальные природные явления: геологический разлом, из которого извергается горящая сера, кислотное озеро и знаменитое &ldquo;северное сияние&rdquo; - свечение тысяч огоньков бирюзового цвета.</p>\r\n\r\n<p>Спуск прямо в кратер действующего вулкана многим может показаться экстремальной и даже опасной затеей, но будьте уверены: это приключение абсолютно безопасно и совершить восхождение может каждый человек с любым уровнем физической подготовки.&nbsp;</p>\r\n\r\n<h2 style=\"text-align: center;\">Восхождение на вулкан: как это будет</h2>\r\n\r\n<p style=\"text-align: justify;\">Попасть на остров Ява можно на пароме, который отходит с Бали в 19:00 часов с порта Гилиманук. Трансфер доставит вас с Семиньяка к порту. Через несколько часов путешествия на пароме, переплыв переправу, все желающие могут отдохнуть и набраться сил для дальнейшего путешествия. Само восхождение начинается в 2:00 ночи. Освещая фонарями широкую дорогу, покрытую толстым слоем вулканической пыли, вы вместе с гидом подниметесь к кратеру вулкана, откуда вам откроется уникальное невероятно красивое зрелище: небо, усыпанное мириадами звезд, яркая луна, мелькающие фонари рабочих, носящих мешки с серой, синие всполохи, образующиеся от реакции серы с кислородом - такое волшебство может подарить только Индонезия!&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">В 5:00 утра, когда взойдет солнце, вашему взору откроется сюрреалистическая картина: самое большое кислотное озеро в мире, окруженное ярко-желтыми окаменелостями природной серы, глина, песок, разломы скал, облака ядовитого газа&hellip; Это поистине фантастическая картина! Заключительный этап путешествия - спуск в лагерь, завтрак и возвращение на пароме на Бали и к &ldquo;точке сбора&rdquo; - Семиньяк.&nbsp;</p>\r\n'),(15,'Снорклинг на Бали в бухте Туламбен в Голубой лагуне - поплавайте вместе с гигантскими скатами! ','снорклинг на Бали, сноклинг на Бали, подводное плавание на Бали, дайвинг на Бали цены, дайвинг на Бали с инструктором','Погружение с инструктором на Бали в живописной бухте Туламбен на затонувший корабль Либерти. Цены на дайвинг на Бали и лучшие места для дайвинга!','Снорклинг с Мантами',1,2,2,5,'60','Предлагаем вам исследовать увлекательный подводный мир Бали и своими глазами увидеть гигантских скатов!','Полное описание','1900',1,800,1900,'5968f53fa6414.jpg','Программа','zagolovok-13',0,0,0,0,0,'<h2 style=\"text-align: center;\">Снорклинг в голубой лагуне: удивительно путешествие в подводный мир Бали</h2>\r\n\r\n<p style=\"text-align: justify;\">Совершить первое погружение в голубой лагуне на Бали может как новичок, так и опытный дайвер. Голубая лагуна, находящаяся в заливе Туламбен, - одно из самых красивых местностей для погружения - недалеко от берега находится затонувший корабль Либерти, &ldquo;обросший&rdquo; коралловыми рифами и кишащий&nbsp;разнообразными формами жизни. Здесь вы можете поплавать с огромными морскими черепахами и крошечными пестрыми рыбками, понаблюдать за многочисленными косяками рыб и скатами-мантами. Здесь нет течения и волн, вода очень теплая - около +30С и прозрачная (видимость от 10 до 20 метров).</p>\r\n\r\n<p style=\"text-align: justify;\">Перед погружением вас ждет подготовительный курс и подробный инструктаж, который поможет вам преодолеть все страхи и сомнения, а также обеспечит вам максимальную безопасность во время погружения. После инструктажа, легкого завтрака и пробного погружения в бассейн вас ждет погружение в океан. Каждый новичок погружается вместе с персональным инструктором. Погружение, как и инструктаж и занятие, проводится небольшими группами (вы и ваши друзья). Благодаря индивидуальному подходу к каждому студенту обеспечивается безопасность и комфорт по время плавания.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Насладиться красотами подводного мира могут все желающие, кто не имеет медицинских противопоказаний. Персональный подход к каждому дайверу, много красочных фотографий, захватывающий и уникальный подводный мир Бали гарантируют вам превосходное настроение!</p>\r\n'),(16,'Морская рыбалка, снорклинг и однодневный круиз на катере к островам Бали. описание, фото, цены туров на остров Бали','рыбалка на бали, рыбалка на бали цены, морской круиз бали, аренда катера на бали, снорклинг бали, ныряние с маской на бали, однодневный круиз бали, однодневный круиз бали цена, морская рыбалка на бали','Морской круиз на катере в акватории Бали подарит вам массу удовольствия. Мы предлагаем вам насыщенную программу, в которую помимо рыбалки входит отдых на белоснежных пляжах, ныряние с маской в безупречно чистой воде и приготовление обеда из вашего улова','Рыбалка на Бали',1,1,2,5,'60','Однодневный круиз в открытом море подарит вам азарт морской рыбалки и чудесный отдых на пляжах Бали!','','1900',1,800,1900,'5968f811da783.jpg','Программа','zagolovok',0,0,0,0,0,'<h2 style=\"text-align: center;\">Морской круиз на Бали: рыбалка, снорклинг и пляжный отдых</h2>\r\n\r\n<p style=\"text-align: justify;\">Для тех, кто желает отвлечься от туристической суеты и провести время в семьи и друзей, мы предлагаем однодневные морские круизы. Вы сможете не только отдохнуть в тесном кругу своих близких людей, вдоволь пообщаться, но и заняться любимым делом - порыбачить! Кроме того, в программу также входит снорклинг (благодаря прозрачной чистой воде ныряние с маской принесет вам море удовольствий!), отдых на белых песках на островах недалеко от Бали и, по желанию, приготовление обеда из вашего же улова.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">В 9:00 утра трансфер из вашего отеля доставит вас в порт Беноа, откуда скоростной катер под управлением опытного капитана быстро доставит вас к живописным спотам для снорклинга и рыбным местам для удачной рыбалки. С океана все красоты Бали и близлежащих островов (ваш путь будет пролегать рядом с островом Нуса Пенида) откроются вам с другой стороны! Пестрые коралловые сады, невероятно красивые бухты с невиданными ранее рыбами, экзотическая морская фауна (морские черепахи, гигантские скаты-манты), кристально-чистая вода вблизи о. Лембонган с его необъятными пляжами с белым песком - все это придется по душе каждому, кто выберет морскую прогулку!&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Организацией процесса рыбалки, снорклинга занимаются гид и капитан судна, они же обеспечат вам комфортное времяпрепровождение, предоставят все необходимое снаряжение и, если вы пожелаете, поделятся с вами секретами удачной морской рыбалки.&nbsp;<br />\r\nНа острове Лембонган, в одном из местных ресторанчиков, повар предложит приготовить на обед ваш улов, а что может быть вкуснее собственноручно пойманной рыбы? К слову, в акватории Нуса Пенида и острова Лембонган водятся тунец, махи-махи, барракуда и даже голубой марлин - мечта всех истинных рыболовов!</p>\r\n\r\n<p style=\"text-align: justify;\">В общей сложности весь морской круиз продлится до обеда, в порт вы вернетесь около 16:00, оттуда вас водитель отвезет обратно в отель (предварительно оговорите это при бронировании тура).&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><strong>Для некоторых отдалённых районов может потребоваться доплата за трансфер.</strong> <strong>Если в одном туре несколько разных адресов, предусмотрите доплату за каждый отдельный адрес или бесплатный трансфер с единого места встречи. Уточните ваш маршрут с менеджером.</strong></p>\r\n'),(17,'','','','Дайвинг в Туламбен',1,2,NULL,5,'6 часов','Побывай в трюме затонувшего корабля США','','нет ограничений ',4,75,90,'598b3ffa402a9.jpg','Затонувший корабль, кораллы','dayving-zatonuvshiy-korabl',1,0,0,1,6,'<p>Совершить первое погружение&nbsp;на Бали&nbsp;может каждый!&nbsp;Одно из самых удивительных&nbsp;мест &mdash; затопленный вблизи&nbsp;берега корабль Либерти. На северо-востоке острова Тихий океан омывает залив&nbsp;Туламбен, где покоится корабль.&nbsp;Это дайв-чек для профессионалов, а для новичков &mdash; комфортные условия, чтобы&nbsp;погружение стало восхитительным и незабываемым!</p>\r\n\r\n<p>Красочный коралловый риф, разросшийся вокруг затонувшего корабля на глубине от 4 до 30 метров на песчаном откосе заворожит бесподобной красотой и разнообразием океанических форм жизни. Вы встретите множество крупных и крошечных рыб, косяки тунца, и, если повезёт, поплаваете с огромными морскими черепахами. В этом месте нет течений и почти нет волн, тёплая вода около 30 градусов &mdash; никто не замёрзнет&nbsp;&mdash;&nbsp;и превосходная видимость на 10&ndash;20 метров.</p>\r\n'),(19,'','','','Параглайдинг',1,1,NULL,NULL,'2 часа','Полет с птицами над красивейшим пляжем Бали','','нет ограничений',4,110,NULL,'5977cb266fce5.jpg','Полет Нуса Дуа, Пандава','paraglayding',0,0,0,0,2,'<p>Параплан это сверхлёгкий планёр-паритель, по конструкции близкий планирующему парашюту. Параплан максимально прост в пилотировании и доступен практически любому&nbsp;здоровому человеку. Подготовка к полёту занимает всего 5 минут!&nbsp;Взлететь на нём можно с любого холма, а сесть на очень небольшие&nbsp;площадки. Парашютом управляет инструктор, поэтому полет для вас будет безопасным и комфортным.</p>\r\n\r\n<p>Полёт на параплане приносит невыразимое чувство вольного&nbsp;полёта&nbsp;без помех в виде кабины. Используя восходящие потоки&nbsp;на параплане можно&nbsp;летать под облаками&nbsp;часами, преодолевая&nbsp;десятки и сотни километров без единой капли горючего. Парапланеризм&nbsp;это возможность увидеть остров с необычного ракурса, вырваться из привычных рамок&nbsp;и&nbsp;открыть&nbsp;мир новых ярких впечатлений, лёгкости, тишины и&nbsp;свободного&nbsp;ветра!</p>\r\n'),(20,'Туры по востоку Бали. Достопримечательности северо-восточной части острова. Описание экскурсий, фото, цена','Ритуалы Бали, культура Бали, балийские храмы, балийские целители, храм в облаках, водный королевский дворец, пещера летучих мышей, шоколадная фабрика, Дворец Тирта Ганга,  храм Пура Лямпуянг, Дворец Таман Уджунг, Храм Летучей Мыши, Гоа Лавах','В ходе этой экскурсии вы узнаете о тысячелетних обычаях северных народов Бали, увидите храм Пура Лямпуянг, водный королевский дворец Тирта Ганга, пещеру летучих мышей Гоа Лавах, шоколадную фабрику и насладитесь яркими красками заката на пляже с черным пес','Северо-восток острова Бали',1,4,NULL,NULL,'','Посети самые необычные храмы северо-восточной части Бали','','5',1,50,NULL,'596e0a6184511.png','Тирта Ганга, Пура Ляпуянг и т.д.','severo-vostok-ostrova-bali',0,0,0,1,10,'<h2 style=\"text-align: center;\">Магия восточного Бали: храмы, дворцы, древние традиции Острова Богов</h2>\r\n\r\n<p style=\"text-align: justify;\">Ровно в 9:00 утра ваш гид заедет за вами в отель, для того чтобы познакомиться и немного рассказать об экскурсии и маршруте, отнеситесь внимательно к его рекомендациям, потому что местная культура имеет свои особенности и вам интересно будет услышать несколько полезных советов до того, как вы отправитесь в путешествие по северо-востоку.</p>\r\n\r\n<p style=\"text-align: justify;\">Приблизительно в 11:00 часов вы попадете в первый храм, который называется Водный Дворец Тирта Ганга. Он был построен по распоряжению раджи Карангасема, который в середине ХХ века был во Франции, где посетил Версальский Дворец, и пораженный красотой увиденного, решил превзойти французов и построить не менее роскошный дворец, утопающий в зелени садов, цветников и скверов, со множеством фонтанов и водоемов.</p>\r\n\r\n<p style=\"text-align: justify;\">В 13:00 вы окажетесь в священном Храме Пура Лямпуянг, красотой построек не уступающему ни одному дворцу на острове. Если вам повезет, вам удастся погрузиться в таинство храмовой священной церемонии сакрального характера, а может быть один из жрецов выполнит ваше заветное желание или даст ответ на вопрос, который вам давно не дает покоя. И если вы преодолеете 1700 ступеней ведущих на храмовую площадку, то вдобавок ко всему перед вами откроется неимоверный вид на горные массивы, острова и Тихий океан.</p>\r\n\r\n<p style=\"text-align: justify;\">В 14:00 вы будете наслаждаться архитектурными изысками и чудесами ландшафтного дизайна другого, не менее великолепного дворца Таман Уджунг. Здесь вы ощутите себя раджой и владыкой Маджапахита, прогуливаясь по тропинкам в садах сказочной красоты Дворца Таман Уджунг.</p>\r\n\r\n<p style=\"text-align: justify;\">К 15:00, после посещения Дворца Таман Уджунг, наш путь приведет вас в Храм Летучей Мыши &nbsp;или Гоа Лавах (на местном диалекте), который на самом деле представляет собой огромную пещеру, населенную летучими мышами, которые на Бали считаются священными животными.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Закончится наша экскурсия около 17:00 в весьма необычном месте - на Шоколадной Фабрике Чарли, где вас поразят эльфийские постройки, в которых живут местные жители, работающие на производстве, и аромат местного шоколада, который изготавливается из здешнего сырья - какао-бобов, растущих на плантациях в окрестностях чудесной сладкой Фабрики Чарли.</p>\r\n\r\n<p style=\"text-align: justify;\">Если у вас останутся силы и будет желание, гид отвезет вас проводить сансет на пустынный пляж с белоснежным песком, где можно будет в кругу друзей поделиться впечатлениями, искупаться в океане, сделать красочные фото или помедитировать в одиночестве, - кому что больше нравится.</p>\r\n'),(21,'Отдых на Бали: остров Нуса Пенида, достопримечательности, водопады ','нуса пенида, бали, водопады нуса пенида, туры бали, бали недорого,  нуса пенида достопримечательности,  остров бали отдых,  отдых на острове бали цены','Путешествие на Бали невозможен без посещения острова Нуса Пенида с его необыкновенной природой: водопадами, флорой и фауной','Нуса Пенида',1,1,NULL,NULL,'','Волшебный отдых на острове Нуса Пенида','','',1,NULL,NULL,'5977c572a9597.jpg','','vodopady-bali',0,0,0,0,0,'<h2 style=\"text-align: center;\">&nbsp;Нуса Пенида. Отдых на райском острове</h2>\r\n\r\n<p style=\"text-align: justify;\">Путешествие на Бали будет неполным, если вы не посетите крупнейший остров, находящийся на юго-востоке Индонезии - Нуса Пенида. Еще до недавнего времени развитие туризма здесь было строго-настрого запрещено властями страны. Именно поэтому здесь вы можете в полной мере насладиться азиатской экзотикой, изучить местную флору и фауну, принять солнечные ванны на снежно-белых песках в тени отвесных скал, понырять с маской, поплавать с гигантскими скатами&nbsp;и получить незабываемые впечатления!&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Небольшой островок Нуса Пенида находится в 12 км к юго-востоку от Бали, но по своей красоте не уступает &ldquo;старшему брату&rdquo;: первозданная природа не оставит ни одного гостя острова равнодушным. Площадь Нуса Пенида чуть более 200 кв.км., что в 6 раз меньше Бали, проживает здесь около 30 тыс. человек, инфраструктура развита не очень хорошо, но это не является помехой для отдыха.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Здесь вы можете погрузиться в самобытную культуру, которой практически не коснулась глобализация, насладиться тишиной и красивейшими закатами в уютных бунгало и вкусной местной едой.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Для любителей дайвинга это райский уголок: сочные краски коралловых рифов и богатая фауна морских глубин поразит ваше воображение! Гигантские черно-синие скаты-манты с размахом крыла 5 метров&nbsp;могут навести страх, но можете не бояться: они питаются только планктоном и всячески стараются избегать контакта с людьми. Если вам повезет, то вы можете увидеть редкую рыбу-луну Мола-Мола, которая с июня по август всплывает на поверхность, чтобы не менее причудливые рыбы-ангелы почистили ее чешую от полипов.&nbsp;</p>\r\n'),(23,' Квадроциклы на Бали: прокатись по джунглям! Поездка для новичков и профессионалов, для любителей скорости и экстремальной езды! Описание, фото, цены на квадроциклы','Квадроциклы Бали, тур на квадроциклах Бали, путешествие по джунглям Бали, поездка на квадроцикле Бали, экстрим на Бали, адреналин на Бали, экстремальные приключения на Бали, экстремальные развлечения Бали','Мечтаете прокатиться по непреодолимым джунглям Индонезии, испытать пьянящий запах скорости по неизведанным тропам? Приключения на квадроциклах по самым сложным и живописным уголкам Бали именно для вас! ','Тур на квадроциклах',1,1,NULL,5,'3','Тур на квадроциклах по тропическим лесам Бали подарит вам адреналин и незабываемые эмоции!','','2',4,85,NULL,'598b8d7a2db71.jpg','обучение, заезд по джунглям','tur-na-kvadrotsiklakh',0,0,0,1,3,'<h2 style=\"text-align: center;\">Скорость, адренали, драйв: все это подарит вам прогулка на квадроцикле по джунглям Бали!</h2>\r\n\r\n<p style=\"text-align: justify;\">Вы не мыслите свою жизнь без острых ощущений? Вас манит все неизведанное, интересное и экстремальное? Тогда поездка на квадроцикле в самом сердце тропических лесов Бали доставит вам настоящее удовольствие!&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Квадроцикл - уникальное транспортное средство, которое предоставит вам возможность побывать в неизведанных уголках Бали в компании друзей. Мы гарантируем вам взрыв эмоций и море впечатлений от пребывания в глубине джунглей наедине с первозданной природой! &nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Утром вас будет ждать микроавтобус, который доставит вас из вашего отеля прямиком к месту действия - горному району острова Бали, густо заросшего тропическими лесами. На месте вы пройдете подробный инструктаж, получите квадроцикл (коробка передач - полуавтомат), все необходимое снаряжение и вместе с инструктором отправитесь покорять джунгли Индонезии. Общая продолжительность маршрута составляет 6,5 км. Перед приключениями в настоящих джунглях вам дадут возможность попрактиковаться в кокосовой роще. После поездки вас ждет душ, сытный обед и трансфер обратно в гостиницу.&nbsp;</p>\r\n'),(24,'Велосипедная прогулка по самым красивым регионам Бали. Описание, фото и цены экскурсий по Бали ','велотур Бали, велосипед Бали, велотуры по Бали, трассы Бали, велотуры для новичков Бали, велотуры для профи Бали, сколько стоит велотур Бали, цены на велотур Бали, поездка на велосипеде по джунглям Бали, поездка на велосипеде по горам Бали, аренда велосип','Велотуры по окрестностям Бали с профессиональным гидом. Познакомьтесь поближе с бытом и культурой местных жителей, мы предлагаем широкий выбор экскурсий по живописным уголкам Индонезии: джунглям, горам, пляжам, ручьям, вдоль рисовых террас и балийских дер','Велотур на Бали',1,1,NULL,5,'5','Велотур по Бали для желающих отдохнуть с пользой для здоровья и насладиться местной экзотикой','','',4,40,NULL,'598c70357a790.jpg','2','velotur-na-bali',0,0,0,1,5,'<p><span style=\"text-align: justify;\">Остров Бали - один из самых живописных островов Индонезии и даже мира, и мы предлагаем интересные экскурсии для любителей активного отдыха и красивых мест. Для поклонников велоспорта и просто любителей прокатиться с ветерком на велосипеде&nbsp;мы подготовили два маршрута по Бали - Китамани-Убуд и рисовые террасы Джатилувих. С особенностью этих маршрутов, длительностью и ценой вы можете ознакомиться в нашем путеводителе.&nbsp;&nbsp;</span></p>\r\n\r\n<div style=\"text-align: justify;\">\r\n<h2 style=\"text-align: center;\">Маршрут первый: Кинтамани-Убуд</h2>\r\n\r\n<p>Для всех желающих отдохнуть с пользой для здоровья и насладиться местной экзотикой эта экскурсия будет более, чем интересной. Утром из отеля вас ожидает трансфер в район Кинтамани, где вы, возле красивейших мест у вулкана Батур, сможете позавтракать и выпить чашечку&nbsp;ароматного&nbsp;кофе. После небольшого отдыха начнется ваше велопутешествие к району Убуд. По дороге вы сможете увидеть то, что недоступно путешественникам из салона машины или автобуса: размеренный образ жизни небольших деревушек, где еще царят старинные традиции, обычаи, обряды, вы сможете понаблюдать за процессом уборки риса на плантациях. Приятный бонус - остановка и возможность сделать памятные фото и видео в понравившихся вам местах. Спуск с небольших возвышенностей, приятный воздух с полей и гор привнесут нотки свежести в ваше путешествие!</p>\r\n\r\n<h2 style=\"text-align: center;\">Маршрут второй: рисовые террасы Джатилувих</h2>\r\n\r\n<p>Эта поездка не такая насыщенная, как предыдущая, но такая же прекрасная и живописная: на своем пути вы сможете созерцать красивейшие индонезийские пейзажи, увидите своими глазами знаменитые рисовые террасы Джатилувиха. Вы&nbsp;сможете максимально приблизиться&nbsp;к балийским деревням и местным жителям, к их устоям, традициям и обычаям, к флоре и фауне острова! &nbsp;</p>\r\n</div>\r\n'),(25,'Рафтинг на Бали - это безопасный сплав по горным рекам, живописные пейзажи и захватывающий дух приключения!','рафтинг на Бали, рафтинг по рекам Бали, рафтинг на реке Аюнг, рафтинг на реке Телага, цены на рафтинг Бали, экстремальные развлечения на Бали, сплав по рекам Бали, сплав на лодке Бали','Совместите активный отдых вместе с созерцанием экзотической природы Индонезии - отправляйтесь в сплав по горным рекам Бали! Мы предоставляем подробные описание туров и экскурсий по Бали, яркие фото и цены на каждую экскурсию','Рафтинг на Бали',1,1,NULL,5,'2','Совместите активный отдых вместе с созерцанием экзотической природы - отправляйтесь в сплав по горным рекам Бали','','2',4,50,NULL,'598b8f5d52ab5.jpg','2','rafting-na-bali',0,0,0,0,2,'<h2 style=\"text-align: center;\">Нескучный отдых на Бали: захватывающий сплав по рекам Телага и Аюнг</h2>\r\n\r\n<p style=\"text-align: justify;\">Если спокойный отдых на пляжах Бали вам наскучил и душа жаждет приключений, тогда рафтинг - это то, что вам нужно! Экстремальный спуск по живописным горным рекам острова, захватывающие дух речные пороги, невероятной красоты ущелья и гроты - такое времяпрепровождение принесет массу удовольствия всем - и взрослым, и детям. Каждый поклонник&nbsp;активного отдыха на природе не останется равнодушными к дикой природе Бали: свисающие лианы в густых тропиках, живописные скалы и гроты, необычные представители фауны&hellip; К тому же, каждого участника сплава будет сопровождать профессиональный инструктор, что сделает приключение безопасным и увлекательным.</p>\r\n\r\n<h2 style=\"text-align: center;\">Приглашаем вас осуществить спуск по рекам Телага и Аюнг&nbsp;</h2>\r\n\r\n<p style=\"text-align: justify;\">Протяженность сплава по реке Телага составляет 14 км: на маршруте, проходящий вдоль скалистых берегов и через крупный водопад, вас ждут крупные пороги, крутые повороты, за которыми открываются виды на рисовые террасы и дикие джунгли. Продолжительность путешествия - около 2,5 часов. Маршрут по живописной реке Аюнг, протяженностью 10 км, достаточно несложный и рафтинг по этой горной речке придется по душе как детям, так и их родителям. Продолжительность путешествия - около 2 часов.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Сплав вы будете совершать вместе с опытными инструкторами и гидами с учетом международных стандартов безопасности. В конце дня вас ждут душ и легкий обед. Советуем взять с собой сменную одежду. Поверьте, эта экскурсия на Бали запомнится вам на всю жизнь!&nbsp;</p>\r\n'),(26,'Катание на слонах на Бали - увлекательная экскурсия по джунглям на самых больших животных в мире!','слоны на бали, парк слонов на бали, катание на слонах, экскурсии на слонах, слона Бали, покататься на слонах бали, катание на слонах цена, стоимость экскурсий на бали, остров бали экскурсия, индонезия стоимость экскурсии ','Прокатиться верхом на слоне по джунглям Бали - это запоминающаяся экскурсия, которая понравится как взрослым, так и детям. На нашем сайте вы можете подробно ознакомиться  с экскурсиями на слонах, фото и стоимостью','Катание на слонах',1,1,NULL,NULL,'2','Не упустите уникальную возможность прокатиться на самых больших животных в мире - индийских слонах!','','',4,60,NULL,'598b8f9f748b0.jpg','1','kataniye-na-slonakh',0,0,0,0,1,'<h2 style=\"text-align: center;\">Прикоснитесь к неизведанному, почувствуйте себя частью природы!</h2>\r\n\r\n<p style=\"text-align: justify;\">Мы предлагаем вам уникальную возможность:&nbsp;прокатиться через джунгли на спине гигантских величественных созданий - индийских слонах! На острове Суматра (шестой по величине остров в мире) находится целый парк слонов, все животные отсюда спасены из лесов, которые подверглись вырубке на Суматре. Сегодня же эти слоны - вымирающий вид.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">На этой экскурсии у вас будет возможность не только приблизиться и погладить этих животных, но и покормить их с рук и сделать несколько снимков на память. После увлекательной поездки вы можете посетить сувенирную лавку, которая предлагает много интересных изделий на тему слонов - поделки из дерева и слоновой кости, сувениры, украшения, картины, одежду.</p>\r\n\r\n<p><strong>Специальная цена для семьи 2 взрослых и 2 детей&nbsp;&nbsp;- 260$</strong></p>\r\n');

/*Table structure for table `tours_photo` */

DROP TABLE IF EXISTS `tours_photo`;

CREATE TABLE `tours_photo` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `photo` varchar(255) DEFAULT NULL COMMENT 'Фотография',
  `title` varchar(255) DEFAULT NULL COMMENT 'Название',
  `tour_id` int(11) DEFAULT NULL COMMENT 'Оборудование',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `sort` int(16) DEFAULT NULL COMMENT 'Сортировка',
  PRIMARY KEY (`id`),
  KEY `product_id` (`tour_id`),
  KEY `status_id` (`status_id`),
  CONSTRAINT `tours_photo_ibfk_2` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`),
  CONSTRAINT `tours_photo_ibfk_3` FOREIGN KEY (`tour_id`) REFERENCES `tours` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=97 DEFAULT CHARSET=utf8;

