/*Table structure for table `tours_price` */

DROP TABLE IF EXISTS `tours_price`;

CREATE TABLE `tours_price` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `tour_id` int(11) DEFAULT NULL COMMENT 'Тур',
  `title` varchar(255) DEFAULT NULL COMMENT 'Название',
  `price` decimal(10,0) DEFAULT NULL COMMENT 'Цена',
  `description` varchar(255) DEFAULT NULL COMMENT 'Описание',
  `top` tinyint(1) DEFAULT NULL COMMENT 'Хит',
  `text` text COMMENT 'Преимущества',
  `status_id` tinyint(1) DEFAULT NULL COMMENT 'Статус',
  `sort` int(10) DEFAULT NULL COMMENT 'Сортировка',
  PRIMARY KEY (`id`),
  KEY `tour_id` (`tour_id`),
  KEY `status_id` (`status_id`),
  CONSTRAINT `tours_price_ibfk_1` FOREIGN KEY (`tour_id`) REFERENCES `tours` (`id`) ON DELETE CASCADE,
  CONSTRAINT `tours_price_ibfk_2` FOREIGN KEY (`status_id`) REFERENCES `status` (`id`)
