/*Table structure for table `tours_type` */

DROP TABLE IF EXISTS `tours_type`;

CREATE TABLE `tours_type` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL COMMENT 'Название категории',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

/*Data for the table `tours_type` */

insert  into `tours_type`(`id`,`title`) values (1,'Активные отдых'),(2,'Водные туры'),(3,'Острова'),(4,'Однодневные туры');

/*Table structure for table `widget` */

DROP TABLE IF EXISTS `widget`;

CREATE TABLE `widget` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT 'Название',
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

/*Data for the table `widget` */

insert  into `widget`(`id`,`name`) values (1,'carousel'),(2,'reviews'),(3,'tours');

/*Table structure for table `widget_data` */

DROP TABLE IF EXISTS `widget_data`;

CREATE TABLE `widget_data` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `widget_id` int(11) DEFAULT NULL COMMENT 'Индификатор',
  `data` text COMMENT 'Содержимое',
  PRIMARY KEY (`id`),
  KEY `widget_id` (`widget_id`),
  CONSTRAINT `widget_data_ibfk_1` FOREIGN KEY (`widget_id`) REFERENCES `widget` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

/*Data for the table `widget_data` */

insert  into `widget_data`(`id`,`widget_id`,`data`) values (1,1,'<h1 class=\"text-white\">Выбери свое Бали</h1>\n'),(2,1,'<p class=\"h6 text-white\">Мы предлагаем самые интересные Туры и Экскурсии на о.Бали</p>\n'),(3,2,'<h3>Отзывы наших клиентов</h3>\n'),(4,2,'<p>Our clients&#39; testimonials are the best proof of our high level of service</p>\n'),(5,3,'<h3>Самые популярные</h3>\n'),(6,3,'<p>Ознакомтесь с нашими Самыми Популярными Турами</p>');

